// LoginPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function LoginPage({ setRole }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [srn, setSrn] = useState(''); // For student SRN
    const [gId, setGId] = useState(''); // For teacher G_ID
    const [userRole, setUserRole] = useState('student'); // Default role is 'student'
    const navigate = useNavigate();

    const handleLogin = () => {
        // Perform validation based on role
        if (userRole === 'student' && srn) {
            setRole('student');
            navigate('/student');
        } else if (userRole === 'teacher' && gId) {
            setRole('teacher');
            navigate('/teacher');
        } else {
            alert("Please enter the required details.");
        }
    };

    return (
        <div>
            <h2>Login</h2>
            
            <label>
                Select Role:
                <select value={userRole} onChange={(e) => setUserRole(e.target.value)}>
                    <option value="student">Student</option>
                    <option value="teacher">Teacher</option>
                </select>
            </label>
            <br />

            {userRole === 'student' ? (
                <div>
                    <label>
                        SRN:
                        <input 
                            type="text" 
                            value={srn} 
                            onChange={(e) => setSrn(e.target.value)} 
                            placeholder="Enter SRN"
                        />
                    </label>
                    <br />
                </div>
            ) : (
                <div>
                    <label>
                        G_ID:
                        <input 
                            type="text" 
                            value={gId} 
                            onChange={(e) => setGId(e.target.value)} 
                            placeholder="Enter G_ID"
                        />
                    </label>
                    <br />
                </div>
            )}

            <button onClick={handleLogin}>Login</button>
            <button onClick={() => alert("Redirect to account creation page")}>
                Create Account
            </button>
        </div>
    );
}

export default LoginPage;
