import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentView() {
    const [data, setData] = useState(null);
    const studentSRN = "SRN001";  // Set this dynamically based on user login or input

    useEffect(() => {
        async function fetchData() {
            const response = await axios.get(`http://localhost:5000/student?srn=${studentSRN}`);
            setData(response.data);
        }
        fetchData();
    }, [studentSRN]);

    if (!data) return <div>Loading...</div>;

    return (
        <div>
            <h2>Student Data</h2>
            {/* Render data here as before */}
        </div>
    );
}

export default StudentView;
