// src/TeacherView.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TeacherView() {
    const [data, setData] = useState(null);

    useEffect(() => {
        async function fetchData() {
            try {
                const response = await axios.get('http://localhost:5000/teacher');
                setData(response.data);
            } catch (error) {
                console.error("Error fetching teacher data", error);
            }
        }
        fetchData();
    }, []);

    if (!data) return <div>Loading...</div>;

    return (
        <div>
            <h2>Teacher Data</h2>
            
            <h3>Guides</h3>
            <ul>
                {data.guides.map((guide) => (
                    <li key={guide.G_id}>{guide.G_name} (ID: {guide.G_id})</li>
                ))}
            </ul>

            <h3>Marksheet</h3>
            <ul>
                {data.marksheets.map((mark) => (
                    <li key={`${mark.SRN}-${mark.G_id}`}>
                        SRN: {mark.SRN}, Total: {mark.Total}, Assessment: {mark.Assessment_Number}
                    </li>
                ))}
            </ul>

            <h3>Panels</h3>
            <ul>
                {data.panels.map((panel) => (
                    <li key={panel.Panel_Id}>
                        Panel {panel.Panel_Id} - Guides: {panel.G_id1}, {panel.G_id2}, {panel.G_id3}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TeacherView;
