import React, { useState } from 'react';
import axios from 'axios';

function TeacherView() {
    const [srn, setSrn] = useState('');  // State for storing SRN input by the user
    const [data, setData] = useState(null);  // State for storing fetched data

    // Fetch data from the server using the provided SRN
    const fetchData = async (srn) => {
        if (!srn) {
            alert("Please enter a valid SRN.");
            return;
        }

        try {
            // Make the GET request to the Flask backend with the SRN
            const response = await axios.get(`http://localhost:5000/teacher?srn=${srn}`);
            setData(response.data);  // Store the response data
        } catch (error) {
            console.error("Error fetching data:", error);
            setData(null);  // Reset data if there's an error
        }
    };

    // Event handler for fetching data when the button is clicked
    const handleFetchClick = () => {
        fetchData(srn);
    };

    return (
        <div>
            <h2>Teacher View</h2>
            <div>
                <label>
                    Enter Student SRN:
                    <input
                        type="text"
                        value={srn}
                        onChange={(e) => setSrn(e.target.value)}  // Update SRN value on change
                        placeholder="Enter SRN"
                    />
                </label>
                <button onClick={handleFetchClick}>Fetch Data</button>
            </div>

            {data ? (
                <div>
                    {/* Render data based on the response */}
                    <h3>Guides</h3>
                    <ul>
                        {data.guides && data.guides.length > 0 ? (
                            data.guides.map((guide) => (
                                <li key={guide.G_id}>
                                    {guide.G_name} (ID: {guide.G_id})
                                </li>
                            ))
                        ) : (
                            <p>No guide data available.</p>
                        )}
                    </ul>

                    <h3>Marksheet</h3>
                    <ul>
                        {data.marksheets && data.marksheets.length > 0 ? (
                            data.marksheets.map((mark) => (
                                <li key={`${mark.SRN}-${mark.G_id}`}>
                                    SRN: {mark.SRN}, Total: {mark.Total}, Assessment: {mark.Assessment_Number}
                                </li>
                            ))
                        ) : (
                            <p>No marksheet data available.</p>
                        )}
                    </ul>

                    <h3>Panels</h3>
                    <ul>
                        {data.panels && data.panels.length > 0 ? (
                            data.panels.map((panel) => (
                                <li key={panel.Panel_Id}>
                                    Panel {panel.Panel_Id} - Guides: {panel.G_id1}, {panel.G_id2}, {panel.G_id3}
                                </li>
                            ))
                        ) : (
                            <p>No panel data available.</p>
                        )}
                    </ul>
                </div>
            ) : (
                <div>No data found for the provided SRN.</div>
            )}
        </div>
    );
}

export default TeacherView;
