// src/TeacherView.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TeacherView() {
    const [data, setData] = useState(null);
    const [srn, setSrn] = useState("");  // State to hold the SRN input by the teacher

    // Function to handle fetching data for a specific SRN
    const fetchData = async (srn) => {
        try {
            const response = await axios.get(`http://localhost:5000/teacher?srn=${srn}`);
            setData(response.data);
        } catch (error) {
            console.error("Error fetching teacher data", error);
            setData(null);
        }
    };

    useEffect(() => {
        if (srn) {
            fetchData(srn); // Fetch data whenever SRN changes
        }
    }, [srn]);

    return (
        <div>
            <h2>Teacher Data</h2>

            <div>
                <label>
                    Enter Student SRN:
                    <input
                        type="text"
                        value={srn}
                        onChange={(e) => setSrn(e.target.value)} // Update SRN state on input change
                        placeholder="Enter SRN"
                    />
                </label>
                <button onClick={() => fetchData(srn)}>Fetch Data</button>
            </div>

            {data ? (
                <div>
                    <h3>Guides</h3>
                    <ul>
                        {data.guides && data.guides.length > 0 ? (
                            data.guides.map((guide) => (
                                <li key={guide.G_id}>
                                    {guide.G_name} (ID: {guide.G_id})
                                </li>
                            ))
                        ) : (
                            <p>No guide data available.</p>
                        )}
                    </ul>

                    <h3>Marksheet</h3>
                    <ul>
                        {data.marksheets && data.marksheets.length > 0 ? (
                            data.marksheets.map((mark) => (
                                <li key={`${mark.SRN}-${mark.G_id}`}>
                                    SRN: {mark.SRN}, Total: {mark.Total}, Assessment: {mark.Assessment_Number}
                                </li>
                            ))
                        ) : (
                            <p>No marksheet data available.</p>
                        )}
                    </ul>

                    <h3>Panels</h3>
                    <ul>
                        {data.panels && data.panels.length > 0 ? (
                            data.panels.map((panel) => (
                                <li key={panel.Panel_Id}>
                                    Panel {panel.Panel_Id} - Guides: {panel.G_id1}, {panel.G_id2}, {panel.G_id3}
                                </li>
                            ))
                        ) : (
                            <p>No panel data available.</p>
                        )}
                    </ul>
                </div>
            ) : (
                <div>No data found for the provided SRN.</div>
            )}
        </div>
    );
}

export default TeacherView;
